cur_frm.fields_dict.property_unit.get_query = function(doc) {
	return {
		filters: {
			property_name: doc.property
		}
	}
}

cur_frm.set_query("income_account", "items", function(doc) {
	return{
		query: "erpnext.accounts.doctype.journal_entry.journal_entry_custom.get_income_account",
		filters: {'company': doc.company}
	}
});
